<script setup>
import { useDarkMode } from '~/composables/useDarkMode'
import { useWindowScroll } from '@vueuse/core'

const { isDark, colors } = useDarkMode()
const { y: scrollY } = useWindowScroll()

const fillColor = computed(()=> isDark.value ? colors.DARK : colors.LIGHT)
const reverseFill = computed(()=> isDark.value ? colors.LIGHT : colors.DARK)
const strokeColor = computed(()=> isDark.value ? colors.DARK_STROKE : colors.LIGHT_STROKE)

// Calculate scroll progress for animation
const scrollProgress = computed(() => {
  // Start animation after scrolling 200px and complete it after scrolling 800px (extended for 5-step sequence)
  const startOffset = 200
  const endOffset = 800
  const rawProgress = Math.max(0, Math.min(1, (scrollY.value - startOffset) / (endOffset - startOffset)))
  
  // Apply easing function for smoother animation (ease-out cubic)
  const progress = 1 - Math.pow(1 - rawProgress, 3)
  return progress
})

// Calculate stroke dash properties for connector paths with sequential animation
const connectorStrokeProps = computed(() => {
  // Get the path length (approximate values for each path)
  const vueConnectorLength = 350 // Approximate length of vue connector path
  const threejsConnectorLength = 350 // Approximate length of threejs connector path
  const finalConnectorLength = 150 // Approximate length of final connector path
  
  // Calculate progress for each animation step (5-step sequential animation)
  // Step 1: Vue & ThreeJS circles (0% - 20%)
  const vueThreejsCircleProgress = Math.min(1, scrollProgress.value / 0.2)
  
  // Step 2: Main connectors (20% - 40%) 
  const mainConnectorProgress = scrollProgress.value <= 0.2 ? 0 : Math.min(1, (scrollProgress.value - 0.2) / 0.2)
  
  // Step 3: TresJS circle (40% - 60%)
  const tresCircleProgress = scrollProgress.value <= 0.4 ? 0 : Math.min(1, (scrollProgress.value - 0.4) / 0.2)
  
  // Step 4: Final connector (60% - 80%)
  const finalConnectorProgress = scrollProgress.value <= 0.6 ? 0 : Math.min(1, (scrollProgress.value - 0.6) / 0.2)
  
  // Step 5: Scene rectangle (80% - 100%)
  const sceneProgress = scrollProgress.value <= 0.8 ? 0 : Math.min(1, (scrollProgress.value - 0.8) / 0.2)
  
  // Independent label animation - starts when main connectors are 10-20% complete
  const labelProgress = mainConnectorProgress >= 0.1 ? Math.min(1, (mainConnectorProgress - 0.1) / 0.9) : 0
  
  // Calculate the dash offset based on progress
  const vueDashOffset = vueConnectorLength * (1 - mainConnectorProgress)
  const threejsDashOffset = threejsConnectorLength * (1 - mainConnectorProgress)
  const finalDashOffset = finalConnectorLength * (1 - finalConnectorProgress)
  
  // Calculate circle circumferences for animation
  const vueCircleRadius = 31.5
  const tresCircleRadius = 31.5
  const sceneRectPerimeter = (61 + 61) * 2 // Rectangle perimeter for scene
  
  const vueCircleCircumference = 2 * Math.PI * vueCircleRadius
  const tresCircleCircumference = 2 * Math.PI * tresCircleRadius
  
  // Calculate dash offsets for each animation step
  const vueCircleDashOffset = vueCircleCircumference * (1 - vueThreejsCircleProgress)
  const threejsCircleDashOffset = vueCircleCircumference * (1 - vueThreejsCircleProgress)
  const tresCircleDashOffset = tresCircleCircumference * (1 - tresCircleProgress)
  const sceneDashOffset = sceneRectPerimeter * (1 - sceneProgress)
  
  // Calculate label rectangle perimeters and dash offsets
  const rendererRectPerimeter = (123 + 24) * 2 // Custom renderer label rectangle perimeter
  const objectsRectPerimeter = (88 + 24) * 2   // 3D Objects label rectangle perimeter
  
  const rendererDashOffset = rendererRectPerimeter * (1 - labelProgress)
  const objectsDashOffset = objectsRectPerimeter * (1 - labelProgress)
  
  return {
    vue: {
      strokeDasharray: `${vueConnectorLength}`,
      strokeDashoffset: vueDashOffset
    },
    threejs: {
      strokeDasharray: `${threejsConnectorLength}`,
      strokeDashoffset: threejsDashOffset
    },
    final: {
      strokeDasharray: `${finalConnectorLength}`,
      strokeDashoffset: finalDashOffset
    },
    circles: {
      vue: {
        strokeDasharray: `${vueCircleCircumference}`,
        strokeDashoffset: vueCircleDashOffset
      },
      threejs: {
        strokeDasharray: `${vueCircleCircumference}`,
        strokeDashoffset: threejsCircleDashOffset
      },
      tres: {
        strokeDasharray: `${tresCircleCircumference}`,
        strokeDashoffset: tresCircleDashOffset
      },
      scene: {
        strokeDasharray: `${sceneRectPerimeter}`,
        strokeDashoffset: sceneDashOffset
      }
    },
    labels: {
      renderer: {
        strokeDasharray: `${rendererRectPerimeter}`,
        strokeDashoffset: rendererDashOffset
      },
      objects: {
        strokeDasharray: `${objectsRectPerimeter}`,
        strokeDashoffset: objectsDashOffset
      }
    }
  }
})
</script>

